1:
proxy_set_header Accept-Encoding "";
sub_filter
'</head>'
'<link rel="stylesheet" type="text/css" href="https://theme-park.dev/css/base/adguard/${TP_THEME}.css">
</head>';
sub_filter_once on;

0:
{
  http_port 8080
  https_port 8443
}
${APP_DNS_NEXTCLOUD}:8080 {
  header Strict-Transport-Security "max-age=15768000;preload"
  redir /.well-known/carddav /remote.php/dav 301
  redir /.well-known/caldav /remote.php/dav 301
  root * /var/www/html
  encode zstd gzip
  php_fastcgi 127.0.0.1:9000 {
    root /var/www/html
    env front_controller_active true
    trusted_proxies ${IP_POD_CIDR_V4}
  }
  file_server
}

{
  log {
    level info
    output stderr
    format console
  }
  http_port 8080
  https_port 8443
  servers {
    trusted_proxies static ${IP_POD_CIDR_V4}
    trusted_proxies_strict
  }
}
${APP_DNS_DAVIS:=sample}:8080 {
  header -Server
  header -X-Powered-By
  header Referrer-Policy no-referrer-when-downgrade
  header X-Content-Type-Options nosniff
  header Strict-Transport-Security "max-age=15768000;preload"
  redir /.well-known/carddav /dav/ 301
  redir /.well-known/caldav /dav/ 301
  root * /var/www/davis/public
  encode zstd gzip
  php_fastcgi 127.0.0.1:9000 {
    trusted_proxies ${IP_POD_CIDR_V4:=sample}
  }
  file_server {
    hide .git .gitignore
  }
}
