CREATE DATABASE IF NOT EXISTS monica;
CREATE USER IF NOT EXISTS '${MONICA_DB_USERNAME}'@'%' IDENTIFIED BY '${MONICA_DB_PASSWORD}';
GRANT ALL PRIVILEGES ON monica.* to '${MONICA_DB_USERNAME}'@'%';

add_header X-Frame-Options SAMEORIGIN;
add_header X-Content-Type-Options nosniff;
add_header X-XSS-Protection "1; mode=block";
add_header Content-Security-Policy "frame-ancestors 'none'";

1:
add_header X-Frame-Options SAMEORIGIN;
add_header X-Content-Type-Options nosniff;
add_header X-XSS-Protection "1; mode=block";
add_header Content-Security-Policy "frame-ancestors 'none'";

0:
{
  log {
    level info
    output stderr
    format console
  }
  http_port 8080
  https_port 8443
  servers {
    trusted_proxies static ${IP_POD_CIDR_V4}
    trusted_proxies_strict
  }
}
${APP_DNS_DAVIS:=sample}:8080 {
  log
  header -Server
  header -X-Powered-By
  header Referrer-Policy no-referrer-when-downgrade
  header X-Content-Type-Options nosniff
  header Strict-Transport-Security "max-age=15768000;preload"
  redir /.well-known/carddav /dav/ 301
  redir /.well-known/caldav /dav/ 301
  root * /var/www/davis/public
  encode zstd gzip
  php_fastcgi 127.0.0.1:9000 {
    trusted_proxies ${IP_POD_CIDR_V4:=sample}
  }
  file_server {
    hide .git .gitignore
  }
}
