CREATE DATABASE IF NOT EXISTS monica;
CREATE USER IF NOT EXISTS '${MONICA_DB_USERNAME}'@'%' IDENTIFIED BY '${MONICA_DB_PASSWORD}';
GRANT ALL PRIVILEGES ON monica.* to '${MONICA_DB_USERNAME}'@'%';

[Interface]
PrivateKey = ${VPN_INTERFACE_PRIVATE_KEY}
Address = ${VPN_INTERFACE_ADDRESS}
DNS = "1.1.1.1"

[Peer]
PublicKey = ${VPN_PEER_PUBLIC_KEY}
AllowedIPs = 0.0.0.0/0
Endpoint = ${VPN_PEER_ENDPOINT}
PersistentKeepalive = 25
