'</head>'
'<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_DOMAIN}/css/base/radarr/radarr-base.css">
<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_DOMAIN}/css/addons/radarr/radarr-darker/radarr-darker.css">
<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_DOMAIN}/css/addons/radarr/radarr-4k-logo/radarr-4k-logo.css">
</head>';
sub_filter_once on;

[[http]]
# Name of the HTTP server, used for display purposes only.
name = "relay-server"
# TCP address to bind to, for HTTP server.
bind-addr = "0.0.0.0:9096"
# Array of InfluxDB&trade; instances to use as backends for Relay.
output = [
    {{- $influxdbReplicaCount := int .Values.influxdb.replicaCount }}
    {{- $influxdbFullname := include "common.names.fullname" . }}
    {{- $influxdbHeadlessServiceName := printf "%s-headless" (include "common.names.fullname" .) }}
    {{- $releaseName := .Release.Namespace }}
    {{- $clusterDomain:= .Values.clusterDomain }}
    {{- range $e, $i := until $influxdbReplicaCount }}
    { name="{{ $influxdbFullname }}-{{ $i }}", location="http://{{ $influxdbFullname }}-{{ $i }}.{{ $influxdbHeadlessServiceName }}.{{ $releaseName }}.svc.{{ $clusterDomain }}:8086/write", timeout="10s"},
    {{- end }}
]

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://themepark.${EXTERNAL_DOMAIN}/css/base/{{ appName }}/{{ theme }}.css"></head>';
sub_filter_once on;

