if [[ -f /data/package.json ]]; then
    npm install --save $(npm outdated | cut -d' ' -f 1 | sed '1d' | xargs -I '$' echo '$@latest' | xargs echo);
else
    echo "Skipping npm updates, no package.json found.";
fi

