proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_DOMAIN}/css/base/whisparr/nord.css"></head>';
sub_filter_once on;

if ($request_uri ~ ^/api/) {
  return 200;
}
if ($request_uri ~ ^/auth/) {
  return 200;
}
if ($remote_addr ~ ^(192\.168\.|10\.)) {
  return 200;
}
return 403;
