0:
linux:
  ipv4_default:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping {target}
    traceroute: traceroute {target}
  ipv6_default:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping6 {target}
    traceroute: traceroute6 {target}
  ipv4_vpn:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping {target}
    traceroute: traceroute {target}
  ipv6_vpn:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping6 {target}
    traceroute: traceroute6 {target}

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://themepark.${EXTERNAL_DOMAIN}/css/base/{{ appName }}/{{ theme }}.css"></head>';
sub_filter_once on;

proxy_set_header X-Real-IP          $remote_addr;

proxy_set_header X-Forwarded-For    $proxy_add_x_forwarded_for;
proxy_set_header X-Forwarded-Proto  https;
proxy_set_header X-VerifiedViaNginx yes;

# Specific for websockets: force the use of HTTP/1.1 and set the Upgrade header
proxy_set_header Upgrade $http_upgrade;
proxy_set_header Connection 'upgrade';
proxy_cache_bypass $http_upgrade;
proxy_set_header X-Forwarded-Proto $scheme;

# Fixes Mixed Content errors.
add_header 'Content-Security-Policy' 'upgrade-insecure-requests';
