0:
{$HD_BASE_URL}

log {
  output stdout
  level WARN
  format console
}

tracing {
  span proxy
}

reverse_proxy /realtime http://hedgedoc-backend:3000
reverse_proxy /api/* http://hedgedoc-backend:3000
reverse_proxy /public/* http://hedgedoc-backend:3000
reverse_proxy /uploads/* http://hedgedoc-backend:3000
reverse_proxy /apidoc/* http://hedgedoc-backend:3000
reverse_proxy /* http://hedgedoc-frontend:3001 {
  header_up X-Forwarded-Proto "https"
}

linux:
  ipv4_default:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping {target}
    traceroute: traceroute {target}
  ipv6_default:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping6 {target}
    traceroute: traceroute6 {target}
  ipv4_vpn:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping {target}
    traceroute: traceroute {target}
  ipv6_vpn:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping6 {target}
    traceroute: traceroute6 {target}
