0:
proxy_hide_header "x-webkit-csp";
proxy_hide_header "content-security-policy";
proxy_hide_header "X-Frame-Options";
proxy_set_header Accept-Encoding "";
sub_filter_once on;

{
  hostname: "${app_url}"
  bind: "0.0.0.0"
  port: 8536
  tls_enabled: true

  setup: {
    admin_username: "${admin_username}"
    admin_password: "${admin_password}"
    site_name: "${site_name}"
  }

  email: {
    smtp_server: "${smtp_hostname}"
    smtp_login: "${smtp_username}"
    smtp_password: "${smtp_password}"
    smtp_from_address: "${smtp_username}"
    tls_type: "tls"
  }

  pictrs: {
    url: "http://pictrs:8080/"
    api_key: "${pictrs_api_key}"
  }
}

module.exports = {
  // Enter self-hosted configuration options here.
  // https://docs.renovatebot.com/self-hosted-configuration/
  autodiscover: true,
  cacheHardTtlMinutes: 240,
  onboardingConfigFileName: ".github/renovate.json",
  onboardingConfig: {
      "$schema": "https://docs.renovatebot.com/renovate-schema.json",
      "extends": [
          "local>gabe565/renovate-config"
      ]
  },
  detectHostRulesFromEnv: true,
  onboardingRebaseCheckbox: true
};
