0:
resolver kube-dns.kube-system.svc.cluster.local;
resolver_timeout 5s;

server {
    listen 80;

    server_name default;

    location /api/ {
        set $backend s4dfarm-farm.default.svc.cluster.local:5000;
        proxy_pass http://$backend;
        include proxy_params;
    }

    location /stats/ {
        set $backend s4dfarm-farm.default.svc.cluster.local:5000;
        proxy_pass http://$backend;
        include proxy_params;
    }

    location /celery/metrics {
        set $backend s4dfarm-celery.default.svc.cluster.local:5000;
        proxy_pass http://$backend/;
        include proxy_params;
    }

    location / {
        gzip on;
        gzip_static on;
        gzip_types text/plain text/css text/javascript application/javascript;
        gzip_disable "msie6";
        root /front;
        try_files $uri /index.html;
        autoindex off;
    }
}

linux:
  ipv4_default:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping {target}
    traceroute: traceroute {target}
  ipv6_default:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping6 {target}
    traceroute: traceroute6 {target}
  ipv4_vpn:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
    ping: ping {target}
    traceroute: traceroute {target}
  ipv6_vpn:
    bgp_aspath: echo "unsupported"
    bgp_community: echo "unsupported"
    bgp_route: ip route get {target}
