0:
proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_PUBLIC_DOMAIN}/css/base/calibreweb/dracula.css"></head>';
sub_filter_once on;

ui = true
listener "tcp" {
  tls_disable = 1
  address = "[::]:8200"
  cluster_address = "[::]:8201"
  telemetry {
    unauthenticated_metrics_access = "true"
  }
}
storage "raft" {
  path = "/vault/data"
  retry_join {
    leader_api_addr = "http://hashi-vault-0.hashi-vault-internal:8200"
  }
  retry_join {
    leader_api_addr = "http://hashi-vault-1.hashi-vault-internal:8200"
  }
  retry_join {
    leader_api_addr = "http://hashi-vault-2.hashi-vault-internal:8200"
  }

autopilot {
    cleanup_dead_servers = "true"
    last_contact_threshold = "200ms"
    last_contact_failure_threshold = "10m"
    max_trailing_logs = 250000
    min_quorum = 3
    server_stabilization_time = "10s"
  }
}
seal "gcpckms" {
  project     = "taylor-cloud"
  region      = "global"
  key_ring    = "vault-unseal"
  crypto_key  = "vault-key"
}
telemetry {
  prometheus_retention_time = "24h",
  disable_hostname = true
}
service_registration "kubernetes" {}
