0:
proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.dev/css/base/sabnzbd/nord.css"></head>';
sub_filter_once on;

1:
my $rel_url = 'https://api.github.com/repos/VictoriaMetrics/victorialogs-datasource/releases/latest';
my ($gzip_url) = grep { /tar\.gz$/ }
                map { $_->{browser_download_url} }
                @{g("$rel_url")->json->{assets}};

say "Downloading and extracting $gzip_url";

my $plugins_dir = "/var/lib/grafana/plugins";
mkdir($plugins_dir);

my $tarfile = "$plugins_dir/plugin.tar.gz";
system('curl', '-L', $gzip_url, '-o', $tarfile);
system('tar', '-xf', $tarfile, '-C', "$plugins_dir/");
unlink($tarfile);
