0:
{{- $items := list
  "// renovate: lsio-docker-mod"
  "ghcr.io/linuxserver/mods:universal-calibre-v7.16.0"
-}} {{- $filtered := list -}} {{- range $item := $items -}}
  {{- if not (hasPrefix "//" $item) -}}
    {{- $filtered = append $filtered $item -}}
  {{- end -}}
{{- end -}} {{- $filtered | join "|" -}}

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_DOMAIN}/css/base/readarr/space-gray.css"></head>';
sub_filter_once on;

location / {
  proxy_set_header Upgrade $http_upgrade;
  proxy_http_version 1.1;
  proxy_set_header X-Forwarded-Host $http_host;
  proxy_set_header X-Forwarded-Proto $scheme;
  proxy_set_header X-Forwarded-For $remote_addr;
  proxy_set_header Host $host;
  proxy_set_header Connection "upgrade";
  proxy_cache_bypass $http_upgrade;
}

server {
  listen 8080;
  server_name probe-eskalation.eu;
  rewrite ^ https://probe-stuff.de$request_uri? permanent;
}
