0:
name suffix .in-addr.arpa. .in-addr.arpa.
answer name auto
answer value (.*)\.cluster\.local\. {1}.${SECRET_PUBLIC_DOMAIN_NAME}

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_PUBLIC_DOMAIN}/css/base/calibreweb/dracula.css"></head>';
sub_filter_once on;

[SERVICE]
  Daemon Off
  Flush {{ .Values.flush }}
  Log_Level {{ .Values.logLevel }}
  Parsers_File /fluent-bit/etc/parsers.conf
  Parsers_File /fluent-bit/etc/conf/custom_parsers.conf
  HTTP_Server On
  HTTP_Listen 0.0.0.0
  HTTP_Port {{ .Values.metricsPort }}
  Health_Check On
