0:
{{- range .Alerts }}
  {{- if ne .Annotations.description "" }}
    {{ .Annotations.description }}
  {{- else if ne .Annotations.summary "" }}
    {{ .Annotations.summary }}
  {{- else if ne .Annotations.message "" }}
    {{ .Annotations.message }}
  {{- else }}
    Alert description not available
  {{- end }}
  {{- if gt (len .Labels.SortedPairs) 0 }}
    <small>
      {{- range .Labels.SortedPairs }}
        <b>{{ .Name }}:</b> {{ .Value }}
      {{- end }}
    </small>
  {{- end }}
{{- end }}
