server {
  listen 8080;
  server_name static.${SECRET_DOMAIN};
  location / {
    proxy_set_header Host $http_host;
    proxy_pass https://s3.services.${SECRET_DOMAIN}:9000/static-content/;
  }
}

proxy_set_header Accept-Encoding "";
sub_filter '</head>' '<link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_CLUSTER_DOMAIN}/css/base/radarr/${THEME_PARK_ARR_THEME}.css"><link rel="stylesheet" type="text/css" href="https://theme-park.${SECRET_CLUSTER_DOMAIN}/css/addons/radarr/radarr-4k-logo/radarr-4k-logo.css"></head>';
sub_filter_once on;
