CREATE DATABASE IF NOT EXISTS monica;
CREATE USER IF NOT EXISTS '${MONICA_DB_USERNAME}'@'%' IDENTIFIED BY '${MONICA_DB_PASSWORD}';
GRANT ALL PRIVILEGES ON monica.* to '${MONICA_DB_USERNAME}'@'%';

location /notifications {
  proxy_http_version 1.1;
  proxy_set_header Upgrade $http_upgrade;
  proxy_set_header Connection $http_connection;

  proxy_set_header Host $host;
  proxy_set_header X-Real-IP $remote_addr;
  proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
  proxy_set_header X-Forwarded-Proto $scheme;

  proxy_pass http://vaultwarden.default.svc:80;
}

[Interface]
PrivateKey = ${VPN_INTERFACE_PRIVATE_KEY}
Address = ${VPN_INTERFACE_ADDRESS}
DNS = "1.1.1.1"

[Peer]
PublicKey = ${VPN_PEER_PUBLIC_KEY}
AllowedIPs = 0.0.0.0/0
Endpoint = ${VPN_PEER_ENDPOINT}
PersistentKeepalive = 25

while true; do
  find /download -mindepth 1 -mmin +15 -type f -exec fuser -s {} \; -o -exec rm -rf {} +;
  sleep 600;
done &
exec /app/media-roller
